#!/bin/bash
# Link to Argos: https://github.com/p-e-w/argos
# Feel nostalgia for old status icons? This script adds in an icon that changes color based on wifi activity

## Vars
rfOut=$(rfkill) #used for both bt and network stuff
wlan=$(echo "$rfOut" | grep wlan | grep " blocked") #value if blocked (off)
wact=$(ifstat | grep -v '0 0' | tail -n +4) #values from ifstat for active network interface
if [[ ! -z $wact ]]; then
    rx=$(echo "$wact" | cut -c 49-56 | xargs) #receive rate
    if [[ ! -z $(echo "$rx" | grep K) ]]; then
        rx=$(( ${rx//K}*1000 ))
    fi
    tx=$(echo "$wact" | cut -c 63-72 | xargs) #transfer rate
    if [[ ! -z $(echo "$tx" | grep K) ]]; then
        tx=$(( ${tx//K}*1000 ))
    fi
else
    rx=0
    tx=0
fi

## Conditionals
if [[ -z "$wlan" ]]; then
    if [[ "$rx" -gt "500" ]] || [[ "$tx" -gt "500" ]]; then
        image=""
    else
        image=""
    fi
else
    image=""
fi

## Display echo
echo -e " | image=$image imageWidth=18"
echo "---"

## Argos click menu thing
if [ "$ARGOS_MENU_OPEN" == "true" ]; then
    if [[ -z "$wlan" ]]; then
        echo "WiFi On"
        echo "Receive: $rx Bytes/s"
        echo "Transmit: $tx Bytes/s"
    else
        echo "WiFi Off"
    fi
else
    echo "Loading..."
fi
