#!/bin/bash
# Link to Argos: https://github.com/p-e-w/argos
# Feel nostalgia for old status icons? This script adds in an icon that changes color based on disk activity
# Requires iostat to be installed. Find it for your distribution.

## Vars
tps=$(iostat -d 1 2 | grep nvme0n1 | tail -1 | cut -c 8-19 | xargs) #disk io stuff

## Conditionals
if [[ "$tps" -gt "25" ]]; then
    if [[ "$tps" -gt "150" ]]; then
        image=""
    else
        image=""
    fi
else
    image=""
fi

## Display echo
echo -e " | image=$image imageWidth=18"
echo "---"

## Argos click menu thing
if [ "$ARGOS_MENU_OPEN" == "true" ]; then
    echo "Disk TPS: $tps Transfers/s"
else
    echo "Loading..."
fi
